import { CONSTANTS } from "../actions";

let listID = 2;
let cardID = 4;

const initialState = [
    {
        title: "BACKLOG",
        id: `list-${0}`,
        cards: [
            {
                id: `C-${0}`,
                text: "Create User Stories"
            },
            {
                id: 1,
                text: "j'ai pas d'idées de texte, c'est super"
            }
        ]
    },
    {
        title: "PLANNING",
        id: `list-${0}`,
        cards: [
            {
                id:0,
                text: "TO-DO tasks"
            },
            {
                id: 1,
                text: "j'ai toujours pas d'idées de tâches, c'est dingue"
            },
            {
                id: 2,
                text: "ça devient difficile d'avoir des idées qui viennent de nulle part, mon cerveau bouille"
            },
            {
                id: 3,
                text: "need to debug rn arghhh ... wouhou I did it "
            }
        ]
    }

];



const listsReducer = (state = initialState, action) => {
    switch (action.type) {

        case CONSTANTS.ADD_LIST:
            const newList = {
                title: action.payload,
                cards: [],
                id: listID
            };
            listID += 1;
            return [...state, newList];

        case CONSTANTS.ADD_CARD:
            const newCard = {
                text: action.payload.text,
                id: cardID
            }
            cardID += 1;

        const newState = state.map(list => {
            if(list.id === action.payload.listID) {
                return {
                    ...list,
                    cards: [...list.cards, newCard]
                }
            } else {
                return list;
            }
        });

        return newState;

        default: 
            return state;
    }
};

export default listsReducer;
